INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (478, 5, 'Но Иисус сказал ученикам: «Вы дайте им есть!» Ученики спросили в ответ: «Как мы можем это сделать? У нас всего лишь пять хлебов и две маленькие рыбки».', 45, 212, 8, 21);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (920, 4, NULL, 17, 294, 3, NULL);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (921, 5, NULL, 17, 294, 3, NULL);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (922, 6, NULL, 17, 294, 3, NULL);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (923, 7, NULL, 17, 294, 3, NULL);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (924, 8, NULL, 17, 294, 3, NULL);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (482, 9, 'После этого ученики собрали оставшуюся еду и наполнили ею 12 корзин. Вся эта еда чудесным образом появилась из пяти хлебов и двух рыб!', 45, 212, 8, 21);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (474, 1, 'Иисус послал Своих апостолов в разные селения, чтобы они проповедовали и учили людей о Божьем Царстве. Когда ученики вернулись туда, где был Иисус, они рассказали Ему обо всём, что сделали. Иисус предложил им отплыть с Ним в тихое место за озером, чтобы немного отдохнуть. Они сели в лодку и отправились на другой берег.', 45, 212, 8, 21);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (477, 4, 'Вечером ученики сказали Иисусу: «Уже поздно. Отпусти людей, чтобы они могли пойти в ближайшие селения и купить себе какую-нибудь еду».', 45, 212, 8, 21);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (479, 6, 'Тогда Иисус велел Своим ученикам сказать людям, чтобы те сели на траву группами по 50 человек.', 45, 212, 8, 21);
INSERT INTO "public"."verses" ("id", "num", "text", "current_step", "chapter_id", "project_id", "project_translator_id") OVERRIDING SYSTEM VALUE VALUES (483, 200, 'Библейская история из Евангелия от Матфея 14:13-21, Евангелия от Марка 6:31-44, Евангелия от Луки 9:10-17 и Евангелия от Иоанна 6:5-15', 45, 212, 8, 21);
