INSERT INTO "public"."progress" ("id", "verse_id", "step_id", "text", "created_at") OVERRIDING SYSTEM VALUE VALUES (1414, 477, 42, 'Вечером ученики сказали Иисусу: «Уже поздно. Отпусти людей, чтобы они могли пойти в ближайшие селения и купить себе какую-нибудь еду».', '2023-04-28 08:17:32.529929');
INSERT INTO "public"."progress" ("id", "verse_id", "step_id", "text", "created_at") OVERRIDING SYSTEM VALUE VALUES (1415, 479, 42, 'Тогда Иисус велел Своим ученикам сказать людям, чтобы те сели на траву группами по 50 человек.', '2023-04-28 08:17:32.529929');
INSERT INTO "public"."progress" ("id", "verse_id", "step_id", "text", "created_at") OVERRIDING SYSTEM VALUE VALUES (1416, 483, 42, 'Библейская история из Евангелия от Матфея 14:13-21, Евангелия от Марка 6:31-44, Евангелия от Луки 9:10-17 и Евангелия от Иоанна 6:5-15', '2023-04-28 08:17:32.529929');
INSERT INTO "public"."progress" ("id", "verse_id", "step_id", "text", "created_at") OVERRIDING SYSTEM VALUE VALUES (1422, 480, 43, 'После этого Иисус взял пять хлебов и две рыбы, посмотрел на небо и поблагодарил Бога за еду.', '2023-04-28 08:17:51.249372');
